sys_script_include.script:
var SystemLogHelper = Class.create();
SystemLogHelper.prototype = {

	initialize: function() {
		this.setTraceability();
	},

	enableTrace: 'false',

	logPrefix: {
		'Info' : '[Info SystemLogHelper]', //logging prefix to identify library log
		'Error' : '[Error SystemLogHelper]'
	},


	setTraceability: function() {
		this.enableTrace = gs.getProperty('x_401282_nsone_int.debug.enable') || 'false';
	},

	/*
	If property x_401282_nsone_int.debug.enable is set to true, any message passed as a parameter,
	will be added to system log as trace information.
	Input Param:
		msg:(type:string) Custom informative message
	*/
	trace: function(prefix,msg){
		if(this.enableTrace.toLowerCase() == "true"){
			msg = msg.replace(/,/g,",\n");
			msg = msg.replace(/\{/g,"\{\n");
			var dt = new GlideDateTime();
			prefix += "(" + dt.getNumericValue() + ")";
			gs.info(prefix + " : \n\n" + msg + "\n\n");	
		}
	},

	test: function(){
		this.trace(this.logPrefix.Error, "Error Test");
		this.trace(this.logPrefix.Info, "Info Test");
	},

	type: 'SystemLogHelper'
};
