sys_script_include.script:
var NSOneIntegration = Class.create();
NSOneIntegration.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	loadNSOneApi:function(){
		var result=new NSOneApiZones().getAllZones();
		gs.info(result);
		var sync = this.getLastSyncUp();
		//gs.info("getLastSyncUp : "+a);
		return sync;
	},

	getLastSyncUp : function(){
		//gs.info("Inside getLastSyncUp");
		var gr = new GlideRecord("syslog_app_scope");  
		gr.addEncodedQuery('sys_scope=79627e08dbbf330051bde4da4b961982');
		gr.orderByDesc('sys_created_on');
		gr.setLimit(1);
		gr.query();
		if (gr.next()) {
			var date = new GlideDateTime(gr.sys_created_on);
			date.getValue();
			var lastSynUp = date.getDisplayValue();
			return lastSynUp;
		}	
	},

	type: 'NSOneIntegration'
});
