sys_ui_page.client_script:
onLoad();
function onLoad(){
	var ga = new GlideAjax('NS1ApiMonitoring');
	ga.addParam("sysparm_name", "getSyncUpData");
	ga.getXML(getAnswer);

	function getAnswer(response) {
		var answer = response.responseXML.documentElement.getAttribute("answer");
		//alert("Answer : "+answer);
		var res = answer.split(",");
		var synUp = res[0];
		var status = res[1];
		var importSet =res[2];
		var nextSchedule =res[3];
		updateDate(synUp);
		updateStatus(status,importSet);
		updateNextSchedule(nextSchedule);
	}
}

function loadData(){
	var ga = new GlideAjax('NS1ApiMonitoring');
	ga.addParam("sysparm_name", "loadNSOneApi");
	ga.getXMLWait();
	//alert(ga.getAnswer());
	updateDate(ga.getAnswer());
}

function updateDate(answer){
	var sec = $[gs.getProperty('x_401282_nsone_int.monitoring.interval')];
	var min = parseInt(sec/60);
	gel('statusTime').innerHTML = answer;
	gel('statusValue').innerHTML = "This page will auto-refresh after every "+ min +" mins";
}

function updateStatus(status,importSet){
	var url = "https://ven03169.service-now.com/sys_import_set.do?sys_id="+importSet;
	gel('NS1status').innerHTML = status;
	//gel('NS1statusDesc').innerHTML = "To check click on link";
	gel('importURL').href = url;
}

function updateNextSchedule(nextSchedule){
	gel('scheduleTime').innerHTML = nextSchedule; 
	gel('statusValue').innerHTML = "Schedule job will run after every";
}
